mapper.select:
SELECT r.push_record_id AS pushRecordId, r.push_genre_id AS pushGenreId, g.push_genre_name AS pushGenreName, r.push_record_title AS pushRecordTitle, r.push_record_content AS pushRecordContent, r.push_record_time AS pushRecordTime, r.quilt_user_id as quiltUserId, r.user_id AS userId, real_name AS realName, r.push_state AS pushState, r.push_data_id AS pushDataId FROM app_push_record AS r LEFT JOIN zt_user AS u ON r.user_id = u.user_id LEFT JOIN app_push_genre AS g ON r.push_genre_id = g.push_genre_id
WHERE 1 = 1
and r.quilt_user_id = #{quiltUserId}
and r.user_id = #{userId}
and r.push_record_id = #{pushRecordId}
order by push_state desc, push_record_time asc
